import { useEffect, useState } from "react";

export default function App() {
  const [products, setProducts] = useState(() => {
    return JSON.parse(localStorage.getItem("products")) || [];
  });
  const [orders, setOrders] = useState(() => {
    return JSON.parse(localStorage.getItem("orders")) || [];
  });

  const [productForm, setProductForm] = useState({
    name: "",
    description: "",
    image: "",
    price: "",
  });

  const [orderForm, setOrderForm] = useState({
    customer: "",
    productId: "",
  });

  // Save products and orders to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("products", JSON.stringify(products));
  }, [products]);

  useEffect(() => {
    localStorage.setItem("orders", JSON.stringify(orders));
  }, [orders]);

  // Handle product form submission
  const handleProductSubmit = (e) => {
    e.preventDefault();
    if (!productForm.name || !productForm.description || !productForm.price) return;
    setProducts([...products, { ...productForm, id: Date.now() }]);
    setProductForm({ name: "", description: "", image: "", price: "" });
  };

  // Handle order form submission
  const handleOrderSubmit = (e) => {
    e.preventDefault();
    if (!orderForm.customer || orderForm.productId === "") return;
    setOrders([
      ...orders,
      {
        id: Date.now(),
        customer: orderForm.customer,
        productId: parseInt(orderForm.productId),
        status: "Received",
      },
    ]);
    setOrderForm({ customer: "", productId: "" });
  };

  // Update order status (Received -> Ready -> Delivered)
  const updateOrderStatus = (id, newStatus) => {
    setOrders(
      orders.map((order) =>
        order.id === id ? { ...order, status: newStatus } : order
      )
    );
  };

  // Group orders by status for rendering
  const groupedOrders = {
    Received: orders.filter((o) => o.status === "Received"),
    Ready: orders.filter((o) => o.status === "Ready"),
    Delivered: orders.filter((o) => o.status === "Delivered"),
  };

  return (
    <div className="max-w-5xl mx-auto p-4 space-y-8">
      <h1 className="text-center text-3xl font-bold text-orange-600 mb-4">Admin Panel</h1>

      {/* Product Form */}
      <form onSubmit={handleProductSubmit} className="bg-white p-4 rounded shadow space-y-3">
        <h2 className="font-semibold text-xl mb-2">Add Product</h2>
        <input
          type="text"
          placeholder="Product Name"
          value={productForm.name}
          onChange={(e) => setProductForm({...productForm, name: e.target.value})}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={productForm.description}
          onChange={(e) => setProductForm({...productForm, description: e.target.value})}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          placeholder="Image URL"
          value={productForm.image}
          onChange={(e) => setProductForm({...productForm, image: e.target.value})}
          className="w-full p-2 border rounded"
        />
        <input
          type="number"
          placeholder="Price"
          value={productForm.price}
          onChange={(e) => setProductForm({...productForm, price: e.target.value})}
          className="w-full p-2 border rounded"
          required
        />
        <button type="submit" className="bg-orange-500 text-white px-4 py-2 rounded hover:bg-orange-600 w-full">Add Product</button>
      </form>

      {/* Order Form */}
      <form onSubmit={handleOrderSubmit} className="bg-white p-4 rounded shadow space-y-3">
        <h2 className="font-semibold text-xl mb-2">Add Order</h2>
        <input
          type="text"
          placeholder="Customer Name"
          value={orderForm.customer}
          onChange={(e) => setOrderForm({...orderForm, customer: e.target.value})}
          className="w-full p-2 border rounded"
          required
        />
        <select
          value={orderForm.productId}
          onChange={(e) => setOrderForm({...orderForm, productId: e.target.value})}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Product</option>
          {products.map((p, idx) => (
            <option key={p.id} value={idx}>{p.name}</option>
          ))}
        </select>
        <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 w-full">Place Order</button>
      </form>

      {/* Orders Display */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {Object.entries(groupedOrders).map(([status, list]) => (
          <div key={status} className="bg-gray-100 p-4 rounded shadow h-max">
            <h3 className="font-bold text-lg mb-3">{status} Orders</h3>
            {list.length === 0 ? <p className="text-gray-500">No orders.</p> : null}
            {list.map((order) => {
              const product = products[order.productId];
              const nextStatus = order.status === "Received" ? "Ready" : order.status === "Ready" ? "Delivered" : null;
              return (
                <div
                  key={order.id}
                  className="bg-white p-3 rounded shadow flex justify-between items-center mb-3"
                >
                  <div>
                    <p className="font-semibold">{order.customer}</p>
                    <p className="text-sm">{product?.name || "Unknown Product"}</p>
                    <p className="text-xs text-gray-600">Status: {order.status}</p>
                  </div>
                  {nextStatus && (
                    <button
                      className={`px-2 py-1 rounded text-white text-sm ${
                        nextStatus === "Ready" ? "bg-yellow-500 hover:bg-yellow-600" : "bg-green-500 hover:bg-green-600"
                      }`}
                      onClick={() => updateOrderStatus(order.id, nextStatus)}
                    >
                      Mark {nextStatus}
                    </button>
                  )}
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
}
